def transitionTransversionRatio(dnalist):
    transitions = 0
    transversion = 0
    dict = {'A': 2, 'C': 1, 'G': -2, 'T': -1}
    list = [3, -3, 1, -1]
    for i in range(len(DNAlist[0])):
        n = dict[DNAlist[0][i]] + dict[DNAlist[1][i]]
        if n == 0:
            transitions += 1
        elif n in list:
            transversion += 1
    return transitions/transversion

